mapper.select:
select menu.*
from (
         select *
         from t_menu
         where type in ('M', 'C')
         order by order_num
     ) menu
         inner join (
    select menu_id
    from t_role_menu
             inner join (
        select id
        from t_role
        where code = #{roleCode}
          and type = #{platformType}
    ) role on role.id = t_role_menu.role_id
) t on t.menu_id = menu.id
