mapper.delete:
delete from t_role_menu where role_id = #{roleID}

    and menu_id in

        #{menuId}

mapper.select:
select role.id, role.code, role.platform_type, role.name
from (
         select *
         from t_role
         where status = 0
           and platform_type = #{type}
     ) role
         inner join (
    select *
    from t_user_role
    where user_id = #{userId}
) user_role on user_role.role_id = role.id
